UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 1, [Text] = N'In the beginning, God created the heavens and the earth.' WHERE [Id] = 1;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 2, [Text] = N'The earth was without form and void, and darkness was over the face of the deep. And the Spirit of God was hovering over the face of the waters.' WHERE [Id] = 2;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 3, [Text] = N'And God said, “Let there be light,” and there was light.' WHERE [Id] = 3;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 4, [Text] = N'And God saw that the light was good. And God separated the light from the darkness.' WHERE [Id] = 4;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 5, [Text] = N'God called the light Day, and the darkness he called Night. And there was evening and there was morning, the first day.' WHERE [Id] = 5;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 6, [Text] = N'And God said, “Let there be an expanse in the midst of the waters, and let it separate the waters from the waters.”' WHERE [Id] = 6;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 7, [Text] = N'And God made the expanse and separated the waters that were under the expanse from the waters that were above the expanse. And it was so.' WHERE [Id] = 7;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 8, [Text] = N'And God called the expanse Heaven. And there was evening and there was morning, the second day.' WHERE [Id] = 8;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 9, [Text] = N'And God said, “Let the waters under the heavens be gathered together into one place, and let the dry land appear.” And it was so.' WHERE [Id] = 9;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 10, [Text] = N'God called the dry land Earth, and the waters that were gathered together he called Seas. And God saw that it was good.' WHERE [Id] = 10;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 11, [Text] = N'And God said, “Let the earth sprout vegetation, plants yielding seed, and fruit trees bearing fruit in which is their seed, each according to its kind, on the earth.” And it was so.' WHERE [Id] = 11;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 12, [Text] = N'The earth brought forth vegetation, plants yielding seed according to their own kinds, and trees bearing fruit in which is their seed, each according to its kind. And God saw that it was good.' WHERE [Id] = 12;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 13, [Text] = N'And there was evening and there was morning, the third day.' WHERE [Id] = 13;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 14, [Text] = N'And God said, “Let there be lights in the expanse of the heavens to separate the day from the night. And let them be for signs and for seasons, and for days and years,' WHERE [Id] = 14;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 15, [Text] = N'and let them be lights in the expanse of the heavens to give light upon the earth.” And it was so.' WHERE [Id] = 15;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 16, [Text] = N'And God made the two great lights—the greater light to rule the day and the lesser light to rule the night—and the stars.' WHERE [Id] = 16;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 17, [Text] = N'And God set them in the expanse of the heavens to give light on the earth,' WHERE [Id] = 17;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 18, [Text] = N'to rule over the day and over the night, and to separate the light from the darkness. And God saw that it was good.' WHERE [Id] = 18;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 19, [Text] = N'And there was evening and there was morning, the fourth day.' WHERE [Id] = 19;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 20, [Text] = N'And God said, “Let the waters swarm with swarms of living creatures, and let birds fly above the earth across the expanse of the heavens.”' WHERE [Id] = 20;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 21, [Text] = N'So God created the great sea creatures and every living creature that moves, with which the waters swarm, according to their kinds, and every winged bird according to its kind. And God saw that it was good.' WHERE [Id] = 21;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 22, [Text] = N'And God blessed them, saying, “Be fruitful and multiply and fill the waters in the seas, and let birds multiply on the earth.”' WHERE [Id] = 22;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 23, [Text] = N'And there was evening and there was morning, the fifth day.' WHERE [Id] = 23;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 24, [Text] = N'And God said, “Let the earth bring forth living creatures according to their kinds—livestock and creeping things and beasts of the earth according to their kinds.” And it was so.' WHERE [Id] = 24;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 25, [Text] = N'And God made the beasts of the earth according to their kinds and the livestock according to their kinds, and everything that creeps on the ground according to its kind. And God saw that it was good.' WHERE [Id] = 25;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 26, [Text] = N'Then God said, “Let us make man in our image, after our likeness. And let them have dominion over the fish of the sea and over the birds of the heavens and over the livestock and over all the earth and over every creeping thing that creeps on the earth.”' WHERE [Id] = 26;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 27, [Text] = N'So God created man in his own image,' WHERE [Id] = 27;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 28, [Text] = N'And God blessed them. And God said to them, “Be fruitful and multiply and fill the earth and subdue it, and have dominion over the fish of the sea and over the birds of the heavens and over every living thing that moves on the earth.”' WHERE [Id] = 28;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 29, [Text] = N'And God said, “Behold, I have given you every plant yielding seed that is on the face of all the earth, and every tree with seed in its fruit. You shall have them for food.' WHERE [Id] = 29;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 30, [Text] = N'And to every beast of the earth and to every bird of the heavens and to everything that creeps on the earth, everything that has the breath of life, I have given every green plant for food.” And it was so.' WHERE [Id] = 30;
UPDATE [dbo].[Verses] SET [TranslationId] = 1, [BookId] = 1, [ChapterNumber] = 1, [VerseNumber] = 31, [Text] = N'And God saw everything that he had made, and behold, it was very good. And there was evening and there was morning, the sixth day.' WHERE [Id] = 31;

